<?phprequire_once "comunes.php";function armar_url($parametros){    if(!isset($parametros['hacer'])){        throw new Exception("armar_url sin parametro hacer");    }    $pares=array();    foreach($parametros as $nombre_parametro=>$valor_parametro){        $pares[]=interpolador(            '#nombre_parametro=#valor_parametro',            array(                'nombre_parametro'=>$nombre_parametro,                'valor_parametro' =>$valor_parametro            )        );    }    return "main.php?".implode('&',$pares);}?>