<?phprequire_once "armador-url.php";function despachable_menu(){    global $modelo;    $enviar=new ArmadorHtml();     $enviar->complejo('<ul>');    foreach($modelo['entidades'] as $entidad_id=>$entidad_def){        $enviar->complejo('<li>#entidad_nombre<ul>',$entidad_def);        $enviar->complejo(            '<li><a href="#url">agregar</a></li><li>listados<ul>',            array('url'=>armar_url(array('hacer'=>'agregar_entidad','entidad_id'=>$entidad_id)))        );        foreach($entidad_def['listados'] as $listado_id=>$listado_def){            $enviar->complejo(                '<li><a href="#url">#listado_nombre</a>',                array(                    'listado_nombre'=>$listado_def['listado_nombre'],                    'url'=>armar_url(array(                        'hacer'=>'listar_entidad',                        'entidad_id'=>$entidad_id,                        'listado_id'=>$listado_id                    ))                )            );        }        $enviar->complejo('</ul>');        $enviar->complejo('</ul>');    }    $enviar->complejo('</ul>');}?>