<?phprequire_once "armador-url.php";function despachable_menu(){    global $modelo;    $enviar=new ArmadorHtml();     $enviar->complejo('<ul>');    foreach($modelo['entidades'] as $id_entidad=>$entidad){        $enviar->complejo('<li>#nombre_entidad<ul>',$entidad);        $enviar->complejo(            '<li><a href="#url">agregar</a></li><li>listados<ul>',            array('url'=>armar_url(array('hacer'=>'agregar_entidad','entidad'=>$id_entidad)))        );        foreach($entidad['listados'] as $id_listado=>$dummy){            $enviar->complejo(                '<li><a href="#url">#listado</a>',                array(                    'listado'=>$id_listado,                    'url'=>armar_url(array(                        'hacer'=>'listar_entidad',                        'entidad'=>$id_entidad,                        'listado'=>$id_listado                    ))                )            );        }        $enviar->complejo('</ul>');        $enviar->complejo('</ul>');    }    $enviar->complejo('</ul>');}?>